import express from "express";
import morgan from "morgan";
import equiposRoutes from "./routes/equipos.routes";
import ligasRoutes from "./routes/ligas.routes";

const app=express();


app.set("port",4000);

app.use(morgan("dev"));
app.use(express.json());
